// Valideer input en bereid Apify calls voor
const input = $input.first().json;

if (!input.url || !input.schoolNaam) {
  throw new Error('Vereist: { "url": "https://school.nl", "schoolNaam": "School Naam" }');
}

// Normaliseer URL
let schoolUrl = input.url.trim();
if (!schoolUrl.startsWith('http')) {
  schoolUrl = 'https://' + schoolUrl;
}

// Parse domain voor scraping scope
const urlObj = new URL(schoolUrl);
const domain = urlObj.hostname;

// Initialiseer result tracking
const trackingData = {
  schoolNaam: input.schoolNaam,
  schoolUrl: schoolUrl,
  domain: domain,
  timestamp: new Date().toISOString(),
  apifyToken: $env.APIFY_TOKEN || 'YOUR_APIFY_TOKEN_HERE',
  results: {
    websiteContent: null,
    contactInfo: null,
    pdfAnalysis: null
  },
  errors: []
};

// Apify actor configuraties
const websiteContentConfig = {
  startUrls: [{ url: schoolUrl }],
  crawlerType: "playwright:firefox",
  maxCrawlPages: 30,
  saveFiles: true,
  saveMarkdown: true,
  saveHtml: false,
  htmlTransformer: "readableText",
  removeElementsCssSelector: "nav, footer, .cookie-banner, .modal, #cookie-consent",
  includeUrlGlobs: [`https://${domain}/**`],
  excludeUrlGlobs: [
    "*/agenda/*",
    "*/calendar/*",
    "*/nieuws/*",
    "*/blog/*"
  ],
  maxCrawlDepth: 3,
  proxyConfiguration: {
    useApifyProxy: true
  }
};

const contactInfoConfig = {
  startUrls: [
    { url: schoolUrl },
    { url: `${schoolUrl}/contact` },
    { url: `${schoolUrl}/team` },
    { url: `${schoolUrl}/organisatie` },
    { url: `${schoolUrl}/over-ons` }
  ].filter(item => item.url),
  maxDepth: 2,
  sameDomain: true,
  considerChildFrames: false,
  maxRequestsPerStartUrl: 10,
  useBrowser: true,
  proxyConfig: {
    useApifyProxy: true
  }
};

return {
  trackingData,
  websiteContentConfig,
  contactInfoConfig
};

// Verwerk Website Content Crawler resultaten
const response = $json;
const trackingData = $input.all()[0].json.trackingData;

if (response.error) {
  trackingData.errors.push({
    actor: 'website-content-crawler',
    error: response.error.message || 'Unknown error'
  });
  return trackingData;
}

// Haal dataset items op
const datasetId = response.data?.defaultDatasetId;
if (!datasetId) {
  trackingData.errors.push({
    actor: 'website-content-crawler',
    error: 'No dataset ID returned'
  });
  return trackingData;
}

trackingData.websiteDatasetId = datasetId;
trackingData.websiteRunId = response.data?.id;

return trackingData;

// Verwerk Contact Info Scraper resultaten
const response = $json;
const trackingData = $input.all()[0].json.trackingData;

if (response.error) {
  trackingData.errors.push({
    actor: 'contact-info-scraper',
    error: response.error.message || 'Unknown error'
  });
  return trackingData;
}

// Haal dataset ID op
const datasetId = response.data?.defaultDatasetId;
if (!datasetId) {
  trackingData.errors.push({
    actor: 'contact-info-scraper',
    error: 'No dataset ID returned'
  });
  return trackingData;
}

trackingData.contactDatasetId = datasetId;
trackingData.contactRunId = response.data?.id;

return trackingData;

// Analyseer en combineer alle scraping resultaten
const websiteData = $input.all()[0].json;
const contactData = $input.all()[1].json;
const trackingData = $input.all()[0].json.trackingData || $input.all()[1].json.trackingData;

// Initialiseer resultaat structuur
const results = {
  school: {
    naam: trackingData.schoolNaam,
    url: trackingData.schoolUrl,
    domain: trackingData.domain,
    timestamp: trackingData.timestamp
  },
  contactpersonen: [],
  contactgegevens: {
    emails: new Set(),
    telefoons: new Set()
  },
  systemen: {
    leerlingvolgsysteem: null,
    administratiesysteem: null
  },
  documenten: [],
  belangrijkeContent: [],
  stats: {
    paginasGescraped: 0,
    contactenGevonden: 0,
    pdfGevonden: 0
  },
  errors: trackingData.errors || []
};

// Verwerk Website Content data
if (websiteData && Array.isArray(websiteData)) {
  websiteData.forEach(page => {
    results.stats.paginasGescraped++;

    // Zoek naar systemen in content
    const content = (page.text || page.markdown || '').toLowerCase();

    // Leerlingvolgsysteem detectie
    const lvsPatterns = [
      { pattern: /parnassys/i, name: 'Parnassys' },
      { pattern: /esis/i, name: 'ESIS' },
      { pattern: /dotcom/i, name: 'DotCom' },
      { pattern: /cito\s*lovs/i, name: 'CITO LOVS' },
      { pattern: /boom\s*lovs/i, name: 'BOOM LOVS' },
      { pattern: /diataal/i, name: 'Diataal' }
    ];

    lvsPatterns.forEach(lvs => {
      if (lvs.pattern.test(content) && !results.systemen.leerlingvolgsysteem) {
        results.systemen.leerlingvolgsysteem = lvs.name;
      }
    });

    // Administratiesysteem detectie
    const adminPatterns = [
      { pattern: /magister/i, name: 'Magister' },
      { pattern: /som\s*today/i, name: 'SOMtoday' },
      { pattern: /zermelo/i, name: 'Zermelo' },
      { pattern: /itslearning/i, name: 'ItsLearning' },
      { pattern: /google\s*workspace/i, name: 'Google Workspace' },
      { pattern: /microsoft\s*365/i, name: 'Microsoft 365' }
    ];

    adminPatterns.forEach(admin => {
      if (admin.pattern.test(content) && !results.systemen.administratiesysteem) {
        results.systemen.administratiesysteem = admin.name;
      }
    });

    // Extract belangrijke content (visie/missie)
    const visieMatch = content.match(/visie[:\s]+([^.\n]{20,300})/i);
    if (visieMatch) {
      results.belangrijkeContent.push({
        type: 'Visie',
        content: visieMatch[1].trim(),
        url: page.url
      });
    }

    const missieMatch = content.match(/missie[:\s]+([^.\n]{20,300})/i);
    if (missieMatch) {
      results.belangrijkeContent.push({
        type: 'Missie',
        content: missieMatch[1].trim(),
        url: page.url
      });
    }

    // Check voor PDF documenten
    if (page.files && Array.isArray(page.files)) {
      page.files.forEach(file => {
        if (file.url && file.url.toLowerCase().endsWith('.pdf')) {
          results.documenten.push({
            naam: file.filename || file.url.split('/').pop(),
            url: file.url,
            gevondenOp: page.url
          });
          results.stats.pdfGevonden++;
        }
      });
    }
  });
}

// Verwerk Contact Info data
if (contactData && Array.isArray(contactData)) {
  contactData.forEach(page => {
    // Emails
    if (page.emails && Array.isArray(page.emails)) {
      page.emails.forEach(email => {
        results.contactgegevens.emails.add(email);
        results.stats.contactenGevonden++;
      });
    }

    // Telefoons
    if (page.phones && Array.isArray(page.phones)) {
      page.phones.forEach(phone => {
        results.contactgegevens.telefoons.add(phone);
        results.stats.contactenGevonden++;
      });
    }

    // Uncertain phones
    if (page.phonesUncertain && Array.isArray(page.phonesUncertain)) {
      page.phonesUncertain.forEach(phone => {
        results.contactgegevens.telefoons.add(phone + ' (onzeker)');
      });
    }

    // LinkedIn profielen als contactpersonen
    if (page.linkedIns && Array.isArray(page.linkedIns)) {
      page.linkedIns.forEach(linkedin => {
        const nameMatch = linkedin.match(/linkedin\.com\/in\/([^/]+)/i);
        if (nameMatch) {
          results.contactpersonen.push({
            naam: nameMatch[1].replace(/-/g, ' '),
            linkedIn: linkedin,
            gevondenOp: page.url
          });
        }
      });
    }
  });
}

// Converteer Sets naar Arrays
results.contactgegevens.emails = Array.from(results.contactgegevens.emails);
results.contactgegevens.telefoons = Array.from(results.contactgegevens.telefoons);

// Bereken kwaliteitsscore
results.kwaliteitsscore = {
  totaal: 0,
  details: {
    websiteGescraped: results.stats.paginasGescraped > 0,
    contactenGevonden: results.stats.contactenGevonden > 0,
    systeemInfoGevonden: !!(results.systemen.leerlingvolgsysteem || results.systemen.administratiesysteem),
    documentenGevonden: results.stats.pdfGevonden > 0
  }
};

results.kwaliteitsscore.totaal = Object.values(results.kwaliteitsscore.details)
  .filter(v => v).length * 25;

return results;

// Check of er PDFs gevonden zijn voor verdere analyse
const results = $input.first().json;

if (results.documenten.length === 0) {
  return {
    ...results,
    pdfAnalysis: {
      status: 'Geen PDFs gevonden',
      documenten: []
    }
  };
}

// Filter relevante PDFs
const relevantePdfPatronen = [
  /schoolgids/i,
  /schoolplan/i,
  /informatiegids/i,
  /jaarplan/i,
  /jaarverslag/i,
  /beleidsplan/i,
  /onderwijsplan/i,
  /visie.*document/i
];

const relevantePdfs = results.documenten.filter(doc => {
  return relevantePdfPatronen.some(pattern => pattern.test(doc.naam));
});

if (relevantePdfs.length === 0) {
  return {
    ...results,
    pdfAnalysis: {
      status: 'Geen relevante PDFs gevonden',
      documenten: results.documenten
    }
  };
}

// Prepareer PDF URLs voor Apify PDF Text Extractor
const pdfUrls = relevantePdfs.slice(0, 5).map(doc => doc.url); // Max 5 PDFs

return {
  ...results,
  pdfUrls: pdfUrls,
  pdfConfig: {
    urls: pdfUrls,
    performChunking: true,
    chunkSize: 2000,
    chunkOverlap: 200
  }
};

// Analyseer PDF content met AI-achtige extractie
const pdfData = $json;
const originalResults = $input.all()[0].json;

if (!pdfData || !Array.isArray(pdfData)) {
  return {
    ...originalResults,
    pdfAnalysis: {
      status: 'PDF extractie mislukt',
      error: 'Geen data ontvangen'
    }
  };
}

// Groepeer chunks per PDF
const pdfGroups = {};
pdfData.forEach(chunk => {
  if (!pdfGroups[chunk.url]) {
    pdfGroups[chunk.url] = [];
  }
  pdfGroups[chunk.url].push(chunk);
});

// Analyseer elke PDF
const pdfAnalyses = [];

Object.entries(pdfGroups).forEach(([url, chunks]) => {
  // Combineer alle chunks
  const fullText = chunks
    .sort((a, b) => a.index - b.index)
    .map(chunk => chunk.text)
    .join(' ')
    .toLowerCase();

  const analysis = {
    url: url,
    naam: url.split('/').pop(),
    gevondenInfo: {}
  };

  // Extract visie
  const visiePatterns = [
    /onze\s+visie[:\s]+([^.\n]{20,500})/i,
    /visie[:\s]+([^.\n]{20,500})/i,
    /wij\s+geloven[:\s]+([^.\n]{20,500})/i
  ];

  for (const pattern of visiePatterns) {
    const match = fullText.match(pattern);
    if (match) {
      analysis.gevondenInfo.visie = match[1].trim();
      break;
    }
  }

  // Extract missie
  const missiePatterns = [
    /onze\s+missie[:\s]+([^.\n]{20,500})/i,
    /missie[:\s]+([^.\n]{20,500})/i,
    /wij\s+streven[:\s]+([^.\n]{20,500})/i
  ];

  for (const pattern of missiePatterns) {
    const match = fullText.match(pattern);
    if (match) {
      analysis.gevondenInfo.missie = match[1].trim();
      break;
    }
  }

  // Extract kernwaarden
  const kernwaardenMatch = fullText.match(/kernwaarden[:\s]+([^.\n]{20,500})/i);
  if (kernwaardenMatch) {
    analysis.gevondenInfo.kernwaarden = kernwaardenMatch[1]
      .split(/[,;]/)
      .map(v => v.trim())
      .filter(v => v.length > 2);
  }

  // Check opnieuw voor systemen
  if (!originalResults.systemen.leerlingvolgsysteem) {
    const lvsMatch = fullText.match(/(parnassys|esis|dotcom|cito.*lovs|boom.*lovs|diataal)/i);
    if (lvsMatch) {
      originalResults.systemen.leerlingvolgsysteem = lvsMatch[1];
      analysis.gevondenInfo.leerlingvolgsysteem = lvsMatch[1];
    }
  }

  if (!originalResults.systemen.administratiesysteem) {
    const adminMatch = fullText.match(/(magister|som.*today|zermelo|itslearning|google.*workspace|microsoft.*365)/i);
    if (adminMatch) {
      originalResults.systemen.administratiesysteem = adminMatch[1];
      analysis.gevondenInfo.administratiesysteem = adminMatch[1];
    }
  }

  // Tel hoeveel belangrijke info gevonden is
  analysis.infoScore = Object.keys(analysis.gevondenInfo).length;

  pdfAnalyses.push(analysis);
});

// Update originele resultaten met PDF analyse
originalResults.pdfAnalysis = {
  status: 'Succesvol',
  aantalGeanalyseerd: pdfAnalyses.length,
  documenten: pdfAnalyses
};

// Update belangrijke content met PDF info
pdfAnalyses.forEach(pdf => {
  if (pdf.gevondenInfo.visie) {
    originalResults.belangrijkeContent.push({
      type: 'Visie (uit PDF)',
      content: pdf.gevondenInfo.visie,
      url: pdf.url
    });
  }
  if (pdf.gevondenInfo.missie) {
    originalResults.belangrijkeContent.push({
      type: 'Missie (uit PDF)',
      content: pdf.gevondenInfo.missie,
      url: pdf.url
    });
  }
});

return originalResults;

// Genereer uitgebreid eindrapport
const results = $input.first().json;
const processingTime = Date.now() - new Date(results.school.timestamp).getTime();

// Bouw rapport structuur
const rapport = {
  school: {
    naam: results.school.naam,
    website: results.school.url,
    domain: results.school.domain,
    timestamp: results.school.timestamp
  },
  samenvatting: {
    kwaliteitsscore: results.kwaliteitsscore.totaal,
    paginasGescraped: results.stats.paginasGescraped,
    contactenGevonden: results.stats.contactenGevonden,
    pdfGevonden: results.stats.pdfGevonden
  },
  contactgegevens: {
    emails: results.contactgegevens.emails || [],
    telefoons: results.contactgegevens.telefoons || []
  },
  contactpersonen: results.contactpersonen || [],
  systemen: results.systemen || {},
  documenten: results.documenten || [],
  belangrijkeInformatie: results.belangrijkeContent || [],
  pdfAnalyse: results.pdfAnalysis || { status: 'Niet uitgevoerd' },
  meta: {
    processingTime: `${Math.round(processingTime / 1000)}s`,
    timestamp: new Date().toISOString()
  },
  aanbevelingen: [],
  technischeDetails: {
    apifyRuns: {
      websiteContentCrawler: results.websiteRunId || 'N/A',
      contactInfoScraper: results.contactRunId || 'N/A'
    },
    errors: results.errors || []
  }
};

// Voeg aanbevelingen toe op basis van resultaten
if (!rapport.systemen.leerlingvolgsysteem) {
  rapport.aanbevelingen.push('Leerlingvolgsysteem niet automatisch gedetecteerd - handmatige verificatie aanbevolen');
}

if (rapport.contactgegevens.emails.length === 0) {
  rapport.aanbevelingen.push('Geen email adressen gevonden - controleer contact pagina handmatig');
}

if (rapport.samenvatting.pdfGevonden === 0) {
  rapport.aanbevelingen.push('Geen PDF documenten gevonden - mogelijk staan belangrijke documenten achter een login');
}

if (rapport.samenvatting.kwaliteitsscore < 75) {
  rapport.aanbevelingen.push('Lage kwaliteitsscore - overweeg handmatige aanvulling van de data');
}

// Genereer Markdown versie
const markdown = `# School Scraping Rapport - ${rapport.school.naam}

## ðŸ“Š Samenvatting
- **Website:** ${rapport.school.website}
- **Kwaliteitsscore:** ${rapport.samenvatting.kwaliteitsscore}
- **Verwerkingstijd:** ${rapport.meta.processingTime}
- **Pagina's gescraped:** ${rapport.samenvatting.paginasGescraped}
- **Contacten gevonden:** ${rapport.samenvatting.contactenGevonden}

## ðŸ“§ Contactgegevens
### Email adressen
${rapport.contactgegevens.emails.length > 0 ? rapport.contactgegevens.emails.map(e => `- ${e}`).join('\n') : '- Geen emails gevonden'}

### Telefoonnummers
${rapport.contactgegevens.telefoons.length > 0 ? rapport.contactgegevens.telefoons.map(t => `- ${t}`).join('\n') : '- Geen telefoonnummers gevonden'}

### Contactpersonen
${rapport.contactpersonen.length > 0 ? rapport.contactpersonen.map(p => `- **${p.naam}** ${p.linkedIn ? `- [LinkedIn](${p.linkedIn})` : ''}`).join('\n') : '- Geen contactpersonen gevonden'}

## ðŸ’» Systemen
- **Leerlingvolgsysteem:** ${rapport.systemen.leerlingvolgsysteem || 'Niet gevonden'}
- **Administratiesysteem:** ${rapport.systemen.administratiesysteem || 'Niet gevonden'}

## ðŸ“„ Belangrijke Informatie
${rapport.belangrijkeInformatie.length > 0 ? rapport.belangrijkeInformatie.map(info => `### ${info.type}\n${info.content}\n*Bron: ${info.url}*`).join('\n\n') : 'Geen visie/missie informatie gevonden'}

## ðŸ“‘ Documenten
${rapport.documenten.length > 0 ? rapport.documenten.map(doc => `- [${doc.naam}](${doc.url})`).join('\n') : '- Geen documenten gevonden'}

## ðŸ” PDF Analyse
Status: ${rapport.pdfAnalyse.status}
${rapport.pdfAnalyse.documenten ? rapport.pdfAnalyse.documenten.map(pdf => `
### ${pdf.naam}
- Info score: ${pdf.infoScore}/5
${Object.entries(pdf.gevondenInfo).map(([key, value]) => `- **${key}:** ${Array.isArray(value) ? value.join(', ') : value}`).join('\n')}
`).join('\n') : ''}

## ðŸ’¡ Aanbevelingen
${rapport.aanbevelingen.length > 0 ? rapport.aanbevelingen.map(a => `- ${a}`).join('\n') : '- Alle belangrijke informatie succesvol gevonden!'}

---
*Gegenereerd met n8n + Apify School Scraper*`;

return {
  json: rapport,
  markdown: markdown,
  binary: {
    rapport: {
      data: Buffer.from(JSON.stringify(rapport, null, 2)).toString('base64'),
      mimeType: 'application/json',
      fileName: `school-rapport-${rapport.school.naam.replace(/\s+/g, '-')}.json`
    },
    markdownRapport: {
      data: Buffer.from(markdown).toString('base64'),
      mimeType: 'text/markdown',
      fileName: `school-rapport-${rapport.school.naam.replace(/\s+/g, '-')}.md`
    }
  }
};
